

CREATE VIEW AllEmployeeView AS 

select `e`.`id` AS `id`,`e`.`first_name` AS `first_name`,`e`.`last_name` AS `last_name`,`e`.`username` AS `username`,`e`.`image` AS `image`,`e`.`email` AS `email`,`e`.`mobile` AS `mobile`,`dep`.`department_name` AS `department`,`des`.`designation_name` AS `designation`,`r`.`role_name` AS `role`,`e`.`dob` AS `dob`,`e`.`address` AS `address`,`e`.`gender` AS `gender`,`m`.`name` AS `martial_status`,`e`.`joining_date` AS `joining_date`,`e`.`terminated_date` AS `terminated_date`,`pay`.`basic_salary` AS `basic_salary`,`e`.`experience` AS `experience`,`s`.`status_name` AS `status` from ((((((`hrm_project`.`employees` `e` join `hrm_project`.`departments` `dep` on(`e`.`department_id` = `dep`.`id`)) join `hrm_project`.`designation` `des` on(`e`.`designation_id` = `des`.`id`)) join `hrm_project`.`roles` `r` on(`e`.`role_id` = `r`.`id`)) join `hrm_project`.`martial_status` `m` on(`e`.`martial_status_id` = `m`.`id`)) join `hrm_project`.`payroll` `pay` on(`e`.`payroll_id` = `pay`.`id`)) join `hrm_project`.`status` `s` on(`e`.`status_id` = `s`.`id`))